"use strict";
{
    //Basic data type of typescript
    //primitive data type
    // string
    const myName = "Mr X";
    //number
    const age = 99;
    //boolean
    const isEmployed = false;
    //undefined
    const x = undefined;
    // null
    const y = null;
    //non-primitive data type
    //array
    let friends = ['a', 'b', 'c'];
    let friendsAge = [11, 12, 13];
    //tuple / ordered-array / controlled-index-array
    let coordinates = [63, 25];
    let person = ["Mr x", 69, true];
}
